begin
wwv_flow_imp_shared.create_plugin(
 p_id=>wwv_flow_imp.id(336243373858883216)
,p_plugin_type=>'ITEM TYPE'
,p_name=>'COM.FOS.CKEDITOR4'
,p_display_name=>'FOS - CKEditor4'
,p_supported_ui_types=>'DESKTOP:JQM_SMARTPHONE'
,p_supported_component_types=>'APEX_APPLICATION_PAGE_ITEMS'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('ITEM TYPE','COM.FOS.CKEDITOR4'),'')
,p_javascript_file_urls=>'#PLUGIN_FILES#js/script#MIN#.js'
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'-- =============================================================================',
'--',
'--  FOS = FOEX Open Source (fos.world), by FOEX GmbH, Austria (www.foex.at)',
'--',
'--  This plug-in offers a Rich Text Editor based on CKEditor4.',
'--',
'--  License: MIT',
'--',
'--  GitHub: https://github.com/foex-open-source/fos-ckeditor4',
'--',
'-- =============================================================================',
'',
'procedure render',
'  ( p_item   in            apex_plugin.t_item',
'  , p_plugin in            apex_plugin.t_plugin',
'  , p_param  in            apex_plugin.t_item_render_param',
'  , p_result in out nocopy apex_plugin.t_item_render_result ',
'  )',
'as',
'    c_name     constant apex_plugin.t_input_name := apex_plugin.get_input_name_for_item;',
'    c_path     constant varchar2(4000)           := nvl(p_plugin.attribute_01, ''https://cdn.ckeditor.com/4.19.0/full-all/'');',
'    c_toolbar  constant varchar2(30)             := nvl(p_item.attribute_01, ''full'');',
'begin',
'    if p_param.value_set_by_controller and p_param.is_readonly ',
'    then',
'        return;',
'    end if;',
'',
'    if p_param.is_readonly or p_param.is_printer_friendly ',
'    then',
'        apex_plugin_util.print_hidden_if_readonly',
'          ( p_item  => p_item',
'          , p_param => p_param',
'          );',
'        ',
'        apex_plugin_util.print_display_only',
'          ( p_item             => p_item',
'          , p_display_value    => p_param.value',
'          , p_show_line_breaks => true',
'          , p_escape           => false',
'          , p_show_icon        => false',
'          );',
'    else',
'        -- create Textarea which will hold the initial value',
'        sys.htp.formtextareaopen',
'          ( cname       => c_name',
'          , nrows       => null',
'          , ncolumns    => null',
'          , cattributes => ''id="'' || c_name || ''" class="rich_text_editor apex-item-textarea"''',
'          );',
'',
'        apex_plugin_util.print_escaped_value(p_param.value);',
'',
'        sys.htp.formtextareaclose;',
'',
'        -- add needed libraries',
'        apex_javascript.add_library',
'          ( p_name      => ''ckeditor''',
'          , p_directory => c_path',
'          );',
'',
'        apex_javascript.add_library',
'          ( p_name      => ''jquery''',
'          , p_directory => c_path || ''/adapters/''',
'          );',
'            ',
'        apex_json.initialize_clob_output;',
'        ',
'        -- compose JSON',
'        apex_json.open_object;',
'        apex_json.write(''itemName'', p_item.name);',
'        apex_json.write(''label'', p_item.plain_label);',
'        apex_json.write(''toolbar'', c_toolbar);',
'        ',
'        if p_item.init_javascript_code is not null ',
'        then',
'            apex_json.write_raw(''jsInitCode'', p_item.init_javascript_code);',
'        end if;',
'        ',
'        apex_json.close_object;',
'        ',
'        apex_javascript.add_onload_code(''FOS.item.ckeditor4('' || apex_json.get_clob_output || '');'');',
'        ',
'        apex_json.free_output;',
'',
'        p_result.is_navigable := true;',
'    end if;',
'end render;'))
,p_api_version=>2
,p_render_function=>'render'
,p_standard_attributes=>'VISIBLE:FORM_ELEMENT:SESSION_STATE:READONLY:SOURCE:ENCRYPT:INIT_JAVASCRIPT_CODE'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_help_text=>wwv_flow_string.join(wwv_flow_t_varchar2(
'<p>This plug-in offers the classic Rich Text Editor functionality we''re all used to in APEX with minor differences such as a newer refreshed theme.</p>',
'<p>Starting in APEX 20.2, the APEX RTE has been upgraded to CKEditor5, which is a complete rewrite of the widget, its API is incompatible with the old version, and notably lacks certain features such as the View Source mode. This plug-in is for those'
||' who rely heavily on the features of CKE4 which are not yet part of CKE5.</p>',
'<p>It is generally recommended to upgrade to CKE5 and use the native APEX RTE. If that''s not possible for your business case, you can use this plug-in instead. Officially, CKEditor4 will still be supported by its creators until 2023.</p>',
'<p>This plug-in is almost identical to the classic Rich Text Editor of APEX, so it can be a drop-in replacement for APEX 20.2 and above.</p>',
'<p>The main differences are:<p>',
'<ul>',
'<li>The default theme is now the newer "Moono-Lisa" which offers a more modern, flat design.</li>',
'<li>The library is loaded from the official CKEditor CDN, but you can also host it yourself, and reference the path in a Component Setting.</li>',
'<li>The editor''s width is now responsive via CSS, not JavaScript, which removes jittering when resizing.</li>',
'</ul>'))
,p_version_identifier=>'22.1.0'
,p_about_url=>'https://fos.world'
,p_plugin_comment=>wwv_flow_string.join(wwv_flow_t_varchar2(
'// Settings for the FOS browser extension',
'@fos-auto-return-to-page',
'@fos-auto-open-files:js/script.js'))
,p_files_version=>138
);
